import Head from 'next/head'

export default function Home() {
  return (
    <div className="container">
      <Head>
        <title>Create Next App</title>
        <link rel="icon" href="/img/favicon.ico" />
      </Head>

      <main>
        <header>
          <img className="logo" src="/img/logo.png" />
          <div className="hero">
            <h1 className="title">
              <div className="redShadows">SUPER</div>
              <div className="blueShadows">FLUSH</div>
            </h1>

            <div className="separator">
              <div className="redShadows" />
              <div className="blueShadows" />
            </div>
          </div>
        </header>

        {/* <p className="description">
          Get started by editing <code>pages/index.js</code>
        </p>

        <div className="grid">
          <a href="https://nextjs.org/docs" className="card">
            <h3>Documentation &rarr;</h3>
            <p>Find in-depth information about Next.js features and API.</p>
          </a>

          <a href="https://nextjs.org/learn" className="card">
            <h3>Learn &rarr;</h3>
            <p>Learn about Next.js in an interactive course with quizzes!</p>
          </a>

          <a
            href="https://github.com/vercel/next.js/tree/master/examples"
            className="card"
          >
            <h3>Examples &rarr;</h3>
            <p>Discover and deploy boilerplate example Next.js projects.</p>
          </a>

          <a
            href="https://vercel.com/import?filter=next.js&utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
            className="card"
          >
            <h3>Deploy &rarr;</h3>
            <p>
              Instantly deploy your Next.js site to a public URL with Vercel.
            </p>
          </a>
        </div> */}
      </main>

      <style jsx>{`
        @font-face {
          font-family: "Acens";
          src: url("/fonts/Acens.ttf") format("truetype");
        }

        .container {
          min-height: 100vh;
          padding: 0 0.5rem;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        main {
          padding: 5rem 0;
          flex: 1;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        a {
          color: inherit;
          text-decoration: none;
        }

        header {
          background: black;
        }

        .logo {
          width: auto;
          height: auto;
        }

        .hero {
        }


        .title {
          font-size: 6rem;
          font-family: Acens;
          text-align: center;
        }

        .title > div {
          background: none;
          display: inline-block;
        }

        .redShadows {
          background-color: rgba(255, 64, 0, 0.75);
          color: rgba(255, 64, 0, 0.75);
          filter:
            drop-shadow(-5px -5px 0 rgba(255, 128, 0, 0.75))
            drop-shadow(-5px -5px 0 rgba(255, 255, 0, 0.75));
        }

        .blueShadows {
          background-color: rgba(0, 64, 255, 0.75);
          color: rgba(0, 64, 255, 0.75);
          filter:
            drop-shadow(-5px -5px 0 rgba(0, 128, 255, 0.75))
            drop-shadow(-5px -5px 0 rgba(0, 255, 255, 0.75));
          margin: 1rem 0rem 0rem 1rem;
          vertical-align: -1rem;
        }

        .separator {
          text-align: center;
        }

        .separator > div {
          width: 125%;
          height: 10px;
        }

        code {
          background: #fafafa;
          border-radius: 5px;
          padding: 0.75rem;
          font-size: 1.1rem;
          font-family: Menlo, Monaco, Lucida Console, Liberation Mono,
            DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace;
        }

        .grid {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-wrap: wrap;

          max-width: 800px;
          margin-top: 3rem;
        }

        .card {
          margin: 1rem;
          flex-basis: 45%;
          padding: 1.5rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid #eaeaea;
          border-radius: 10px;
          transition: color 0.15s ease, border-color 0.15s ease;
        }

        .card:hover,
        .card:focus,
        .card:active {
          color: #0070f3;
          border-color: #0070f3;
        }

        .card h3 {
          margin: 0 0 1rem 0;
          font-size: 1.5rem;
        }

        .card p {
          margin: 0;
          font-size: 1.25rem;
          line-height: 1.5;
        }

        .logo {
          height: 1em;
        }

        @media (max-width: 600px) {
          .grid {
            width: 100%;
            flex-direction: column;
          }
        }
      `}</style>

      <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  )
}
